mapper.select:
select count(s.id) from support s left join malfunction m on s.malfunction_id=m.id left join user u on u.id=s.create_by where (s.refuse_staff is null or s.refuse_staff not like CONCAT('%,',#{user_id},'|%') and s.refuse_staff not like CONCAT(#{user_id},'|%')) and s.status=1 and m.status not in(1,4,5,6) and s.m_status != 2
and s.create_by=#{create_by}
and s.create_by != #{user_id} and ((s.support_type = 1 and ((s.support_staff like CONCAT('%,',#{response},',%')) or (s.support_staff like CONCAT(#{response},',%')) or (s.support_staff like CONCAT('%,',#{response})) or (s.support_staff like CONCAT(#{response})))) or (s.support_type = 2 and m.department_id=#{department_id} and (s.support_staff is null or ((LENGTH(s.support_staff)-LENGTH(REPLACE(s.support_staff,',','')))+1) < s.support_number)))

select s.id,m.location as name,s.support_type as type,from_unixtime(s.support_at,'%Y-%m-%d %T') as support_at, s.respond_staff as respond_staff,u.user_name as create_by,s.status as status,s.terminate_at from support s left join malfunction m on s.malfunction_id=m.id left join user u on u.id=s.create_by where (s.refuse_staff is null or s.refuse_staff not like CONCAT('%,',#{user_id},'|%') and s.refuse_staff not like CONCAT(#{user_id},'|%')) and s.status=1 and m.status not in(1,4,5,6) and s.m_status != 2
and s.create_by=#{create_by}
and s.support_at < #{support_at}
and s.create_by != #{user_id} and ((s.support_type = 1 and ((s.support_staff like CONCAT('%,',#{response},',%')) or (s.support_staff like CONCAT(#{response},',%')) or (s.support_staff like CONCAT('%,',#{response})) or (s.support_staff like CONCAT(#{response})))) or (s.support_type = 2 and m.department_id=#{department_id} and (s.support_staff is null or ((LENGTH(s.support_staff)-LENGTH(REPLACE(s.support_staff,',','')))+1) < s.support_number)))
order by s.support_at desc limit 0,#{pageSize}
